import{p as u}from"./page-21e76907.js";import{_ as F,c as E,o as t,G as a}from"./index-3a17f0ec.js";const b={...u},C=""+new URL("图片1-0b8cb489.png",import.meta.url).href,B=""+new URL("图片2-50f163a4.png",import.meta.url).href,d=""+new URL("图片3-c60cbcd5.png",import.meta.url).href,r=""+new URL("图片4-d7459894.png",import.meta.url).href,A=""+new URL("图片5-0207180d.png",import.meta.url).href,p=""+new URL("图片6-a6be5d6b.png",import.meta.url).href,e=""+new URL("图片7-b37b1b10.png",import.meta.url).href,D=""+new URL("图片8-169f2d0c.png",import.meta.url).href,_=""+new URL("图片9-2cf10ab3.png",import.meta.url).href,o=""+new URL("图片10-a6353a28.png",import.meta.url).href,s=""+new URL("图片11-8c288b59.png",import.meta.url).href,m=""+new URL("图片12-df1b3132.png",import.meta.url).href,n=""+new URL("图片13-1109522d.png",import.meta.url).href,i=""+new URL("图片14-92388b4d.png",import.meta.url).href,c=""+new URL("图片15-5b1321a1.png",import.meta.url).href;const l={class:"box"},v=a('<h2 id="15.公网卫星便携站" data-v-45bb18db>15.公网卫星便携站</h2><h3 id="15.1设备介绍" data-v-45bb18db>15.1设备介绍</h3><p data-v-45bb18db>以 BWP500A 型便携式卫星通信终端为例，操作人员通过智能手机连接到终端热点后就可以对便携终端进行控制与操作。</p><h3 id="15.2接口介绍" data-v-45bb18db>15.2接口介绍</h3><img src="'+C+'" alt="" data-v-45bb18db><img src="'+B+'" alt="" data-v-45bb18db><h3 id="15.3设备组装" data-v-45bb18db>15.3设备组装</h3><p data-v-45bb18db>（1）取出便携终端主机和天线副叶瓣。</p><img src="'+d+'" alt="" data-v-45bb18db><p data-v-45bb18db>（2）依次安装天线副叶瓣，安装左右两侧叶瓣时，先锁紧中间锁扣。一只手按住支撑杆另一只手打开天线到有阻力的位置，并固定。</p><img src="'+r+'" alt="" data-v-45bb18db><p data-v-45bb18db>（3）调整天线支撑杆角度，安装馈源。</p><img src="'+A+'" alt="" data-v-45bb18db><p data-v-45bb18db>（4）连接射频线、电源线。</p><img src="'+p+'" alt="" data-v-45bb18db><h3 id="15.4对星入网操作" data-v-45bb18db>15.4对星入网操作</h3><img src="'+e+'" alt="" data-v-45bb18db><p data-v-45bb18db>（1）卫星通 APP 的侧边栏上点击“对准卫星”操作按钮，即可进入对星界面，如左边图所示。界面显示当前接收到的卫星信号强度，且以语音形式实时播报，此外还显示所对卫星的名称，便携终端所在当地经度、当地纬度、天线仰角、天线方位角和天线极化角，用户根据界面显示的方位和俯仰等参数来调节便携终端，完成辅助对星。界面左滑将会出现我们的对星辅助工具如中间图和右边图所示。</p><img src="'+D+'" alt="" data-v-45bb18db><p data-v-45bb18db>（2）终端提供数据通信功能，建立起卫星链路后，便可以通过终端网口进行数据交互</p><img src="'+_+'" alt="" data-v-45bb18db><p data-v-45bb18db>（3）终端提供接入指挥中心网络功能，建立起卫星链路后，可以通过终端的 WIFI 或者有线网口连接接入指挥中心的网络</p><p data-v-45bb18db>（4）修改移动指挥终端网络配置使用 Ctrl+H，呼出管理员命令提示符，输入“explorer“</p><img src="'+o+'" alt="" data-v-45bb18db><p data-v-45bb18db>（5）打开控制面板，选择“网络与共享中心”，点击“更改适配器选项”</p><img src="'+s+'" alt="" data-v-45bb18db><p data-v-45bb18db>（6）右击“以太网”，选择“属性”</p><img src="'+m+'" alt="" data-v-45bb18db><p data-v-45bb18db>（7）双击“Internet 协议版本 4（TRC/IP4）</p><img src="'+n+'" alt="" data-v-45bb18db><p data-v-45bb18db>（8）点击“自动获得 IP 地址”与“自动获得 DNS 服务器地址”， 最后确定。</p><img src="'+i+'" alt="" data-v-45bb18db><p data-v-45bb18db>（9）在移动指挥箱的网络设置中，删掉数据。保存配置即可使用互联 网连接终端。</p><img src="'+c+'" alt="" data-v-45bb18db>',34),g=[v];function h(f,R,w,L,U,x){return t(),E("div",l,g)}const $=F(b,[["render",h],["__scopeId","data-v-45bb18db"]]);export{$ as default};
